import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import PomodoroTimer from "./PomodoroTimer.js"
import PomodoroSettings from "./PomodoroSettings"

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(true);
  const [pomodoro, setPomodoro] = useState({focus: 1500, break: 300, time: 1500});

  const updatePomodoro = (type) => {
    if (type === "decrease-focus") {
      console.log(type);
      const time = Math.max(pomodoro.focus - 300, 300);
      setPomodoro({...pomodoro, focus: time})
    }
    if (type === "increase-focus") {
      console.log(type);
      const time = Math.min(pomodoro.focus + 300, 3600);
      setPomodoro({...pomodoro, focus: time})
    }
    if (type === "decrease-break") {
      console.log(type);
      const time = Math.max(pomodoro.break - 60, 60);
      setPomodoro({...pomodoro, break: time})
    }
    if (type === "increase-break") {
      console.log(type);
      const time = Math.min(pomodoro.break + 60, 900);
      setPomodoro({...pomodoro, break: time})
    }
  }

  useInterval(
    () => {
      const nextSecond = pomodoro.time - 1; 
      setPomodoro({...pomodoro, time: nextSecond});
      console.log(nextSecond);
      console.log(pomodoro);
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  return (
    <div className="pomodoro">
      <PomodoroSettings
        pomodoro={pomodoro} playPause={playPause}
        isTimerRunning={isTimerRunning} updatePomodoro={updatePomodoro}
      />
      <PomodoroTimer pomodoro={pomodoro} />
    </div>
  );
}

export default Pomodoro;
