import React, { useState }from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import Pomodoro from "./Pomodoro";

function PomodomoTimer ({pomodoro, isTimerRunning, onFocus}) {
  //get minutes and seconds for each value (seconds not really necessary, but were useful for testing)
  let timeMinutes = Math.floor(pomodoro.time / 60);
  let timeSeconds = pomodoro.time - timeMinutes * 60;
  let focusMinutes = Math.floor(pomodoro.focus / 60);
  let focusSeconds = pomodoro.focus - focusMinutes * 60;
  let breakMinutes = Math.floor(pomodoro.break / 60);
  let breakSeconds = pomodoro.break - breakMinutes * 60;

  function formatTime(string,pad,length) {
    return (new Array(length+1).join(pad)+string).slice(-length);
  }

  const timeString = formatTime(timeMinutes,'0',2)+':'+formatTime(timeSeconds,'0',2);
  const focusString = formatTime(focusMinutes,'0',2)+':'+formatTime(focusSeconds,'0',2);
  const breakString = formatTime(breakMinutes,'0',2)+':'+formatTime(breakSeconds,'0',2);

  let focusBreakMessage = "Focusing";
  let focusBreakTime = "25:00";
  let percentageTime = 0;
  if (onFocus) {
    focusBreakMessage = "Focusing";
    focusBreakTime = focusString;
    percentageTime = (1 - pomodoro.time / pomodoro.focus) * 100;
  } else {
    focusBreakMessage = "On Break"
    focusBreakTime = breakString;
    percentageTime = (1 - pomodoro.time / pomodoro.break) * 100;
  }
  console.log(percentageTime);

  return (
    <div>
      <div className="row mb-2">
        <div className="col">
          <h2 data-testid="session-title">{focusBreakMessage} for {focusBreakTime} minutes</h2>
          <p className="lead" data-testid="session-sub-title">
            {timeString} remaining
          </p>
        </div>
      </div>
      <div className="row mb-2">
        <div className="col">
          <div className="progress" style={{ height: "20px" }}>
            <div
              className="progress-bar"
              role="progressbar"
              aria-valuemin="0"
              aria-valuemax="100"
              aria-valuenow={percentageTime} // TODO: Increase aria-valuenow as elapsed time increases
              style={{ width: percentageTime }} // TODO: Increase width % as elapsed time increases
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default PomodomoTimer;
