import React, { useState }from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import Pomodoro from "./Pomodoro";

function PomodomoTimer ({pomodoro, isTimerRunning, onFocus}) {
  let minutes = Math.floor(pomodoro.time / 60);
  let seconds = pomodoro.time - minutes * 60;

  function formatTime(string,pad,length) {
    return (new Array(length+1).join(pad)+string).slice(-length);
  }

  let focusBreakMessage = "Focusing";
  if (onFocus) {
    focusBreakMessage = "Focusing";
  } else {
    focusBreakMessage = "On Break"
  }

  const timeString = formatTime(minutes,'0',2)+':'+formatTime(seconds,'0',2);

  return (
    <div>
      <div className="row mb-2">
        <div className="col">
          {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
          <h2 data-testid="session-title">{focusBreakMessage} for 25:00 minutes</h2>
          {/* TODO: Update message below to include time remaining in the current session */}
          <p className="lead" data-testid="session-sub-title">
            {timeString} remaining
          </p>
        </div>
      </div>
      <div className="row mb-2">
        <div className="col">
          <div className="progress" style={{ height: "20px" }}>
            <div
              className="progress-bar"
              role="progressbar"
              aria-valuemin="0"
              aria-valuemax="100"
              aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
              style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default PomodomoTimer;
