import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import PomodoroTimer from "./PomodoroTimer.js"
import PomodoroSettings from "./PomodoroSettings"

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [pomodoro, setPomodoro] = useState({focus: 5, break: 5, time: 61});
  const [onFocus, setOnFocus] = useState(true);


  const updatePomodoro = (type) => {
    if (type === "decrease-focus") {
      console.log(type);
      const newTime = Math.max(pomodoro.focus - 1, 1);
      setPomodoro({...pomodoro, focus: newTime, time: newTime})
    }
    if (type === "increase-focus") {
      console.log(type);
      const newTime = Math.min(pomodoro.focus + 1, 3600);
      setPomodoro({...pomodoro, focus: newTime, time: newTime})
    }
    if (type === "decrease-break") {
      console.log(type);
      const newTime = Math.max(pomodoro.break - 1, 1);
      setPomodoro({...pomodoro, focus: newTime, time: newTime})
    }
    if (type === "increase-break") {
      console.log(type);
      const newTime = Math.min(pomodoro.break + 1, 900);
      setPomodoro({...pomodoro, focus: newTime, time: newTime})
    }
  }

  useInterval(
    () => {
      const nextSecond = pomodoro.time - 1;
      console.log(nextSecond);
      if (nextSecond === 0) {
        if (onFocus) {
          setPomodoro({...pomodoro, time: pomodoro.break});
        } else {
          setPomodoro({...pomodoro, time: pomodoro.focus});
        }
        setOnFocus(!onFocus);
      } else {
        setPomodoro({...pomodoro, time: nextSecond});
      }
      console.log(nextSecond);
      console.log(pomodoro);
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  return (
    <div className="pomodoro">
      <PomodoroSettings
        pomodoro={pomodoro} playPause={playPause}
        isTimerRunning={isTimerRunning} updatePomodoro={updatePomodoro}
      />
      <PomodoroTimer pomodoro={pomodoro} isTimerRunning={isTimerRunning} onFocus={onFocus}/>
    </div>
  );
}

export default Pomodoro;
